
public class IfElseIfExample {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
       int a=4;
       int b=5;
       int c=6;
       if(a>b && a>c)
       {
    	   System.out.println("a is greater");
       }
       else if(b>c) {
    	  
    	   System.out.println("b is greater");
       }
       else {
    	   System.out.println("c is greater");
       }
    }
}
